import axios from 'axios';
import React, { useState } from 'react';

import { useNavigate } from 'react-router-dom';

 export default function EditUser() {

    const navigate = useNavigate();
    const [user,setUser] = useState(
        {
            name:"",
            username:"",
            email:""

        }
    );


    const {name,username,email} = user;

    const onInputChange=(e)=>{

        setUser({...user,[e.target.name]:e.target.value});
    }

    const onSumbit= async (e)=>{
        e.preventDefault();
        await axios.put("http://localhost:8080/super/user",user);
        navigate("/");
    }

  return (
    <div
    className='conteiner'>
        <div className='row'>
            <div className='col-md-6 offset-md-3 border rounded p-4 mt-2 shadow'>
                <h2>Edit User</h2>

                <form onSumbit={onSumbit()}>
                    <div className='mb-3'> 
                        <label htmlFor='Name' className='form-label'Name></label>
                        <input type='text' className='form-control' placeholder='Enter your name' name='name' value={name}
                        onChange={onInputChange()}></input>
                    </div>

                    <div className='mb-3'> 
                        <label htmlFor='Username' className='form-label'Name></label>
                        <input type='text' className='form-control' placeholder='Enter your username' name='username' value={username}
                        onChange={e=>onInputChange(e)}></input>
                    </div>

                    <div className='mb-3'> 
                        <label htmlFor='Name' className='form-label'Name></label>
                        <input type='text' className='form-control' placeholder='Enter your e-mail' name='name' value={email}
                        onChange={e=>onInputChange(e)}></input>
                    </div>
                <button type='submit' className='btn btn-outline-primary'>Submit</button>
                <button type='submit' className='btn btn-outline-danger'>Cancel</button>

                </form>
            </div>
        </div>
        EditUser</div>
  )
}
