import React from 'react'
import { Link } from 'react-router-dom'
import { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';



export default function ViewUser() {


    const [user,setUser] = useState({
        name:"",
        username:"",
        email:""
      });
      
      const {id} = useParams();

      useEffect(()=>{
        loadData();
      },[]);
      
      const loadData = async()=>{
      
        const res = await axios.get(`htt[//localhost:8080/users/${id}`);
        setUser(res);
      }


  return (
    <div className='container'>
    <div className='row'>
      <div className='col-md-6 offset-md-3 border rounded p-4 mt-2 shadow'>
        <h2 className='text-center'>Register User</h2>
        <div className='card'>
            <div className='card-header'>
                Details of user
                <ul className='list-group list-group-flush'>
                    <li className='list-group-item'><b>Name: {user.name}</b></li>
                    <li className='list-group-item'><b>UserName {user.username}</b></li>
                    <li className='list-group-item'><b>E-mail {user.email}</b></li>
                </ul>
            </div>
        </div>
    <Link className="btn btn-primary my-2" to={`/`}>Back to home</Link>
        </div>
        </div>
        </div>
  )
}
